// ProductDetail.js

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import products from "../data/product";

const ProductDetail = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const productDetails = await products(); // Replace with your actual data fetching logic
        const selectedProduct = productDetails.find(
          (item) => String(item.product_id) === productId
        );

        if (!selectedProduct) {
          // Redirect to the product list if the product is not found
          navigate("/");
        } else {
          setProduct(selectedProduct);
        }
      } catch (error) {
        console.error("Error fetching product details:", error);
      }
    };

    fetchProductDetails();
  }, [productId, navigate]);

  if (!product) {
    // You can display a loading indicator or handle the loading state here
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{product.name}</h2>
      {/* Display other product details as needed */}
      <p>Description: {product.description}</p>
      <p>Category: {product.category}</p>
      <p>Price: {product.price}</p>
      {/* Add more details as needed */}
    </div>
  );
};

export default ProductDetail;
