import React, { useEffect, useState } from "react";
import "./header.css";
import { NavLink } from "react-router-dom";
import axios from "axios";
import users from "../data/userData";


const Headers = () => {
  const [userdata, setUserdata] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const fetchedUsers = await users();
        setUserdata(fetchedUsers);
      } catch (error) {
        console.error("Error fetching users:", error);
        setError("Failed to fetch user data. Please try again."); // Set a user-friendly error message
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);
  return (
    <>
    <header className="bg-gray-800 py-4 px-6 flex items-center justify-between">
      <nav>
        <div className="left flex items-center">
          <h1 className="text-white text-2xl font-bold">BuyNSellHub</h1>
        </div>
        <div className="right flex items-center space-x-6">
          <ul className="hidden md:flex">
            
              {Object?.keys(userdata)?.length > 0 ? (
                <>
                  <li>
                    <NavLink to="/home">Home</NavLink>
                  </li>
                  <li>
                  <NavLink to="/">Logout</NavLink>
                  {/* <li onClick={logout}>Logout</li> */}
                  </li>
                  <li>
                    <img
                      src={userdata?.image}
                      style={{ width: "50px", borderRadius: "50%" }}
                      alt=""
                    />
                  </li>
                </>
              ) : (
                <li>
                  <NavLink to="/login">Login</NavLink>
                </li>
              )}
            </ul>
          </div>
        </nav>
      </header>
    </>
  );
};

export default Headers;
