import React, { useEffect, useState } from "react";
import axios from "axios";
import "./header.css"; // Link to your own CSS file if needed
import { NavLink } from "react-router-dom";
import { useDispatch } from "react-redux";

import { setLogin } from "../userData/store";

const Headers = () => {
  const [userdata, setUserdata] = useState({});
  const dispatch = useDispatch();

  useEffect(() => {
    const getUser = async () => {
      try {
        const response = await axios.get("http://localhost:8080/login/sucess", {
          withCredentials: true,
        });

        if (response.data.message) {
          if (response.data.token) {
            localStorage.setItem("token", response.data.token);
          }
        }

        setUserdata(response.data.user);
        dispatch(setLogin({ user: response.data.user, token: response.data.token }));
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    getUser();
  }, []);

  return (
    <header className="bg-gradient py-4 px-6 flex items-center justify-between">
      <nav>
        <div className="left flex items-center">
          <h1 className="text-white text-2xl font-bold">BuyNSellHub</h1>
        </div>
        <div className="right flex items-center space-x-6">
          <ul className="hidden md:flex">
            {Object.keys(userdata).length > 0 ? (
              <>
                <li>
                  <NavLink to="/home">Home</NavLink>
                </li>
                <li>
                  <NavLink to="/add-product">Add Product</NavLink>
                </li>
                <li>
                  <NavLink to="/">Logout</NavLink>
                </li>
                <li>
                  <a href="/user-profile">
                    <img
                      className="header-image"
                      src={userdata?.image}
                      alt="User Profile"
                    />
                  </a>
                </li>
              </>
            ) : (
              <li>
                <NavLink to="/login">Login</NavLink>
              </li>
            )}
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Headers;
