import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import products from "../data/product";

const UserProductDetails = () => {
  const { productId } = useParams();
  const [productDetails, setProductDetails] = useState(null);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const allProducts = await products();
        console.log("All Products : ", allProducts);
        const selectedProduct = allProducts.find(
          (product) => product.id === productId
        );

        if (!selectedProduct) {
          console.error(`Product with ID ${productId} not found.`);
          return;
        }

        setProductDetails(selectedProduct);
      } catch (error) {
        console.error("Error fetching product details:", error.message);
      }
    };

    fetchProductDetails();
  }, [productId]);
  const handleAcceptOffer = async (productId, offerId) => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/products/acceptOffer/${productId}/${offerId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            offerStatus: "accepted",
          }),
        }
      );

      if (response.ok) {
      } else {
        console.error("Error accepting offer:", response.statusText);
      }
    } catch (error) {
      console.error("Error accepting offer:", error);
    }
  };
  
  return (
    <div className="flex max-w-screen-lg mx-auto mt-8">
      {/* Left div with image */}
      <div className="flex-shrink-0 w-1/3 p-8 bg-white rounded-md shadow-md">
        <img
          src={productDetails.productPath}
          alt={productDetails?.name}
          className="w-full h-auto"
        />
      </div>

      {/* Right div with price and offers */}
      <div className="flex-grow p-8 bg-white rounded-md shadow-md">
        {productDetails ? (
          <div>
            <h3 className="text-2xl font-bold mb-4">{productDetails.name}</h3>
            <p className="text-xl mb-4">
              Current Price: ${productDetails.price}
            </p>

            {/* Offers section */}
            <div>
              {productDetails.offers.map((offer, index) => (
                <div key={index} className="mb-4">
                  <p className="text-lg">Offer {index + 1}</p>
                  <p>Price: ${offer.offerAmount}</p>
                  <button className="bg-green-500 text-white px-4 py-2 mr-2">
                    Accept
                  </button>
                  <button
                    className="bg-red-500 text-white px-4 py-2"
                    onClick={handleAcceptOffer}
                  >
                    Reject
                  </button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <p>Loading product details...</p>
        )}
      </div>
    </div>
  );
};

export default UserProductDetails;
