import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Headers from "./Header";
import products from "../data/product";

const Home = () => {
  const [userdata, setUserdata] = useState({});

  const navigate = useNavigate();
  console.log("response", userdata);

  const getUser = async () => {
    try {
      const response = await axios
        .get("http://localhost:8080/login/sucess", {
          withCredentials: true,
        })
        .then((res) => {
          if (res.data.message) {
            if (res.data.token) {
              localStorage.setItem("token", res.data.token);
            }
          }
        });
      setUserdata(response.data.user);
      console.log(userdata);
    } catch (error) {
      console.log("error", error);
    }
  };

  useEffect(() => {
    getUser();
  }, []);

  const [productsList, setProductsList] = useState([]);

  useEffect(() => {
    const fetchProductsData = async () => {
      const fetchedProducts = await products();
      setProductsList(fetchedProducts);
    };

    fetchProductsData();
  }, []);
  const handleBuyNow = (productId) => {
    // Find the selected product by ID
    const selectedProduct = productsList.find((product) => product.id === productId);

    // Navigate to the product detail page with the product ID as a parameter
    navigate(`/product/${productId}`, { state: { product: selectedProduct } });
  };
  return (
    <div>
      <Headers />
      <Link to="/add-product" className="block mb-4 p-2 bg-blue-500 text-white">
        Add Product
      </Link>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {productsList &&
          productsList.length > 0 &&
          productsList.map((item, index) => (
            <div
              key={index}
              className="max-w-md mx-auto mb-8 overflow-hidden bg-white rounded shadow-lg"
            >
              <img
                src={item.productPath}
                alt={item.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h3 className="text-xl font-bold mb-2">{item.name}</h3>
                <p className="text-gray-700 text-sm mb-4">{item.description}</p>
                <p className="text-gray-700 text-sm">
                  Category: {item.category} | ID: {item.studentId}
                </p>
                <div className="flex items-center justify-between mt-4">
                  <p className="text-green-600 font-bold text-sm">
                    Price: {item.price}
                  </p>
                  <button
                   onClick={() => handleBuyNow(item.id)}
                    className="px-3 py-2 text-sm font-medium text-white bg-blue-700 rounded-md hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300"
                  >
                    Buy Now
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
