import React from "react";

const ProductOffer = ({ selectedProduct }) => {
  return (
    <div className="mt-4">
      <h4 className="text-lg font-bold">Offer for {selectedProduct.name}:</h4>
      <p>{selectedProduct.offer}</p>
    </div>
  );
};

export default ProductOffer;
