import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";

const ProductDetail = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [updatedPrice, setUpdatedPrice] = useState(null);
  const [userMadeOffer, setUserMadeOffer] = useState(false);
  const user = useSelector((state) => state.user);

  useEffect(() => {
    const fetchProductDetails = async () => {
      // Fetch product details from your API
      try {
        const response = await fetch(`http://localhost:8080/api/products/${productId}`);
        if (response.ok) {
          const selectedProduct = await response.json();

          setProduct(selectedProduct);
          setUpdatedPrice(selectedProduct.price);
          
          // Check if the user has already made an offer for this product
          const userOffer = selectedProduct.offers.find((offer) => offer.userId === user.email);
          setUserMadeOffer(!!userOffer);
        } else {
          console.error(`Error fetching product details: ${response.statusText}`);
        }
      } catch (error) {
        console.error("Error fetching product details:", error);
      }
    };

    fetchProductDetails();
  }, [productId, user.email]);

  const handlePriceChange = (event) => {
    setUpdatedPrice(parseFloat(event.target.value));
  };

  const handleUpdatePrice = async () => {
    if (userMadeOffer) {
      console.error("User has already made an offer for this product.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/api/products/updateOffer/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userOffer: updatedPrice,
          userId: user.email,
        }),
      });

      if (response.ok) {
        const updatedProduct = await response.json();
        setProduct(updatedProduct);
        setUserMadeOffer(true); // Set the flag to indicate that the user has made an offer
      } else {
        console.error("Error updating product offer:", response.statusText);
      }
    } catch (error) {
      console.error("Error updating product offer:", error);
    }
  };

  if (!product) {
    return (
      <div>
        <h2>Product Not Found</h2>
      </div>
    );
  }
  return (
    <div className="flex items-center justify-center h-screen">
      <div className="max-w-md p-4 flex">
        <img src={product.productPath} alt={product.name} className="w-1/2 h-auto mr-4" />
        <div className="w-1/2">
          <h2 className="text-2xl font-bold mb-4">{product.name}</h2>
          <p className="text-gray-700 text-sm mb-4">{product.description}</p>
          <p className="text-gray-700 text-sm">
            Category: {product.category} | ID: {product.studentId}
          </p>

          <div className="flex items-center mb-4">
            <label className="mr-2">Price:</label>
            <input
              type="range"
              min="0"
              max={product.price *3}
              step="1" 
              value={updatedPrice || product.price}
              onChange={handlePriceChange}
            />
            <span className="ml-2">{updatedPrice || product.price}</span>
          </div>
          <button onClick={handleUpdatePrice} className="px-4 py-2 bg-blue-500 text-white rounded-md focus:outline-none">
            Make Offer
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
