import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import axios from "axios";

const Headers = () => {
  const [userdata, setUserdata] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const getUser = async () => {
    try {
      const response = await axios.get("http://localhost:8080/login/success", {
        withCredentials: true,
      });

      setUserdata(response.data.user);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    // Uncomment this when you want to implement the logout functionality
    // window.open("http://localhost:8080/logout", "_self");
  };

  useEffect(() => {
    getUser();
  }, []);

  return (
    <>
      <header className="bg-blue-500 p-4">
        <nav className="container mx-auto flex justify-between items-center">
          <div className="text-white text-2xl font-semibold">BuyNSellHub</div>
          <div className="text-white">
            {loading ? (
              <p>Loading...</p>
            ) : error ? (
              <p>Error: {error}</p>
            ) : (
              <ul className="flex space-x-4">
                {Object.keys(userdata).length > 0 ? (
                  <>
                    <li>
                      <NavLink to="/home" className="text-white hover:text-gray-300">
                        Home
                      </NavLink>
                    </li>
                    <li>
                      <NavLink to="/" onClick={logout} className="text-white hover:text-gray-300">
                        Logout
                      </NavLink>
                    </li>
                    <li>
                      <img
                        src={userdata.image}
                        style={{ width: "50px", borderRadius: "50%" }}
                        alt=""
                      />
                    </li>
                  </>
                ) : (
                  <li>
                    <NavLink to="/login" className="text-white hover:text-gray-300">
                      Login
                    </NavLink>
                  </li>
                )}
              </ul>
            )}
          </div>
        </nav>
      </header>
    </>
  );
};

export default Headers;
