import React from "react";

const ProductDetail = ({ product }) => {
  console.log("Product : ",product)
  if (!product) {
    return null; // Return null if there is no selected product
  }

  return (
    <div className="mt-4 p-4 border rounded-md shadow-md">
      <h2 className="text-2xl font-bold mb-4">{product.name}</h2>
      <img
        src={product.productPath}
        alt={product.name}
        className="w-full h-48 object-cover mb-4"
      />
      <p className="text-gray-700 text-sm mb-4">{product.description}</p>
      <p className="text-gray-700 text-sm">
        Category: {product.category} | ID: {product.studentId}
      </p>
      <p className="text-green-600 font-bold text-sm mb-4">
        Price: {product.price}
      </p>
      {/* Add more details or customize the content based on your needs */}
    </div>
  );
};

export default ProductDetail;
