import React, { useEffect, useState } from "react";
import "./login.css";

const Login = () => {
  const [alertDisplayed, setAlertDisplayed] = useState(false);

  useEffect(() => {
    const displayAlert = () => {
      const errorType = new URLSearchParams(window.location.search).get(
        "error"
      );
      if (errorType === "DomainNotAllowed" && !alertDisplayed) {
        alert("Domain not allowed");
        setAlertDisplayed(true);
      }
    };

    // Check localStorage to see if the alert has been displayed before
    const isAlertDisplayed = localStorage.getItem("alertDisplayed");

    if (isAlertDisplayed !== "true") {
      displayAlert();
      // Set the flag in localStorage to prevent future alerts
      localStorage.setItem("alertDisplayed", "true");
    }
  }, [alertDisplayed]);

  const loginwithgoogle = () => {
    window.open("http://localhost:8080/auth/google/callback", "_self");
  };

  return (
    <div className="login-page">
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <div className="form">
        <button className="login-with-google-btn" onClick={loginwithgoogle}>
          SIGN IN WITH GOOGLE
        </button>
      </div>
    </div>
  );
};
export default Login;
