import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Headers from "./Header";
import products from "../data/product";

const Home = () => {
  const [userdata, setUserdata] = useState({});

  const navigate = useNavigate();
  console.log("response", userdata);

  const getUser = async () => {
    try {
      const response = await axios
        .get("http://localhost:8080/login/sucess", {
          withCredentials: true,
        })
        .then((res) => {
          if (res.data.message) {
            if (res.data.token) {
              localStorage.setItem("token", res.data.token);
            }
          }
        });
      setUserdata(response.data.user);
      console.log(userdata);
    } catch (error) {
      console.log("error", error);
    }
  };

  useEffect(() => {
    getUser();
  }, []);

  const [productsList, setProductsList] = useState([]);

  useEffect(() => {
    const fetchProductsData = async () => {
      const fetchedProducts = await products();
      setProductsList(fetchedProducts);
    };

    fetchProductsData();
  }, []);
  
  const handleBuyNow = (productId) => {
    const selectedProduct = productsList.find(
      (product) => product.id === productId
    );

    navigate(`/product/${productId}`, { state: { product: selectedProduct } });
  };
  return (
    <div>
      <Headers />
      <Link to="/add-product" className="block mb-4 p-2 bg-blue-500 text-white">
        Add Product
      </Link>
      <Link
        to="/view-product"
        className="block mb-4 p-2 bg-blue-500 text-white"
      >
        View Product
      </Link>
    </div>
  );
};

export default Home;
