import React, { useState, useEffect } from "react";
import axios from "axios";

const UserProfile = ({ userId }) => {
  const [userProfile, setUserProfile] = useState({});
  const [userProducts, setUserProducts] = useState([]);

  

  return (
    <div className="max-w-md mx-auto p-8 bg-white rounded-md shadow-md">
      <h2 className="text-2xl font-bold mb-4">{userProfile.name}'s Profile</h2>
      <div className="mb-4">
        <label className="text-gray-600">Email:</label>
        <p className="text-black">{userProfile.email}</p>
      </div>
      <h3 className="text-xl font-bold mb-2">Products:</h3>
      <ul>
        {userProducts.map((product) => (
          <li key={product.id} className="mb-2">
            {product.name} - {product.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserProfile;
