import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Headers from "./Header";
import products from "../data/product";
import Fuse from "fuse.js";

const ViewProduct = () => {
  const [productsList, setProductsList] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const fuse = new Fuse(productsList, {
    keys: ["name"],
    threshold: 0.3,
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProductsData = async () => {
      const fetchedProducts = await products();
      setProductsList(fetchedProducts);
      setFilteredProducts(fetchedProducts);
    };

    fetchProductsData();
  }, []);

  const handleSearch = (event) => {
    const query = event.target.value.toLowerCase();
    setSearchQuery(query);

    if (query.length > 0) {
      const results = fuse.search(query);
      setFilteredProducts(results.map((r) => r.item));
    } else {
      setFilteredProducts(productsList);
    }
  };

  const filterProductsByCategory = (category) => {
    if (category === "All") {
      setFilteredProducts(productsList);
    } else {
      const filtered = productsList.filter((product) => product.category === category);
      setFilteredProducts(filtered);
    }
  };

  const handleCategoryChange = (event) => {
    const category = event.target.value;
    setSelectedCategory(category);
    filterProductsByCategory(category);
  };

  const handleViewDetails = (productId) => {
    const product = productsList.find((item) => item.id === productId);

    if (product) {
      setSelectedProduct(product);
      navigate(`/product/${productId}`);
    } else {
      console.error(`Product with id ${productId} not found.`);
    }
  };

  return (
    <div>
      <Headers />

      <div className="flex justify-center my-4">
        {/* ... (existing code) */}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredProducts.map((item) => (
          <div key={item.id} className="max-w-md mx-auto overflow-hidden bg-white rounded-md shadow-md hover:shadow-lg">
            {/* ... (existing code) */}
            <button
              onClick={() => handleViewDetails(item.id)}
              className="px-4 py-2 bg-blue-500 text-white rounded-md focus:outline-none"
            >
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewProduct;
