import React, { useState, useEffect } from "react";
import "./login.css";

const Login = () => {
  useEffect(() => {
    const displayAlert = () => {
      const storedAlert = localStorage.getItem("alertShown");

      // Check if the alert has not been shown and there is an alert message in the URL
      if (!storedAlert) {
        const alertMessage = new URLSearchParams(window.location.search).get(
          "alert"
        );
        if (alertMessage) {
          alert(decodeURIComponent(alertMessage));

          // Set the flag in localStorage to indicate that the alert has been shown
          localStorage.setItem("alertShown", "true");
        }
      }
    };

    displayAlert();
  }, []); // Empty dependency array ensures it runs only once when the component mounts

  const loginwithgoogle = () => {
    window.open("http://localhost:8080/auth/google/callback", "_self");
  };

  return (
    <div className="login-page">
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <div className="form">
        <button className="login-with-google-btn" onClick={loginwithgoogle}>
          SIGN IN WITH GOOGLE
        </button>
      </div>
    </div>
  );
};
export default Login;
