import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import products from "../data/product";
import { Link } from "react-router-dom";

const UserProfile = () => {
  const [userProducts, setUserProducts] = useState([]);
  const [userOfferedProducts, setUserOfferedProducts] = useState([]);
  const user = useSelector((state) => state.user);

  useEffect(() => {
    const fetchProductsData = async () => {
      const fetchedProducts = await products();

      const userAddedProducts = fetchedProducts.filter(
        (product) => product.studentId === user.email
      );

      const offeredProducts = fetchedProducts.filter((product) =>
        product.offers.some((offer) => offer.userId === user.email)
      );

      setUserProducts(userAddedProducts);
      setUserOfferedProducts(offeredProducts);
    };

    fetchProductsData();
  }, [user.email]);

  return (
    <div className="flex max-w-screen-lg mx-auto mt-8">
    {/* Left side - User Profile */}
    <div className="flex-shrink-0 w-1/3 p-8 bg-white rounded-md shadow-md flex flex-col items-center justify-center">
      <h2 className="text-2xl font-bold mb-4">{user.displayName}'s Profile</h2>
      <div className="mb-4">
        <p className="text-black">{user.email}</p>
      </div>
    </div>
  
    {/* Right side - Two Divisions */}
    <div className="flex-grow p-8 bg-white rounded-md shadow-md flex flex-col">
      {/* Top Division - User's Products */}
      <div className="mb-8">
        <h3 className="text-xl font-bold mb-4">Your Added Products:</h3>
        {userProducts.map(({ id, name, price }) => (
          <div key={id} className="mb-4">
            <h4 className="text-lg font-bold">{name}</h4>
            <p className="text-gray-500">Current Price: ${price}</p>
            <Link to={`/userproduct/${id}`} className="block mb-2 p-2 bg-blue-500 text-white">
              View Product
            </Link>
          </div>
        ))}
      </div>
  
      {/* Bottom Division - User's Offers */}
      <div>
        <h3 className="text-xl font-bold mb-4">Products with Your Offers:</h3>
        {userOfferedProducts.map(({ id, name, price, offers }) => (
          <div key={id} className="mb-4">
            <h4 className="text-lg font-bold">{name}</h4>
            <p className="text-gray-500">Current Price: ${price}</p>
  
            {offers?.length > 0 && (
              <div>
                <h5 className="text-md font-bold mb-2">Your Offer Details:</h5>
                <ul>
                  {offers
                    .filter((offer) => offer.userId === user.email)
                    .map(({ offerAmount, offerStatus }, offerIndex) => (
                      <li key={offerIndex}>
                        Amount: ${offerAmount}, Status: {offerStatus}
                      </li>
                    ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  </div>
  
  );
};

export default UserProfile;
