import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import products from "../data/product";

const ProductDetail = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProductDetails = async () => {
      const productList = await products();
      const selectedProduct = productList.find((item) => item.id === parseInt(productId, 10));

      if (selectedProduct) {
        setProduct(selectedProduct);
      } else {
        console.error(`Product with id ${productId} not found.`);
      }
    };

    fetchProductDetails();
  }, [productId]);

  if (!product) {
    // Handle the case when product is not available
    return (
      <div>
        <h2>Product Not Found</h2>
      </div>
    );
  }

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">{product.name}</h2>
      <img src={product.productPath} alt={product.name} className="w-full h-64 object-cover mb-4" />
      <p className="text-gray-700 text-sm mb-4">{product.description}</p>
      <p className="text-gray-700 text-sm">
        Category: {product.category} | ID: {product.studentId}
      </p>
      <p className="text-green-600 font-bold text-lg mb-4">Price: {product.price}</p>
      {/* Add more details as needed */}
    </div>
  );
};

export default ProductDetail;
