import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import products from "../path-to-products"; // Update the path to the products file

const UserProductDetails = () => {
  const { productId } = useParams();
  const [productDetails, setProductDetails] = useState(null);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const allProducts = await products(); // Fetch all products
        const selectedProduct = allProducts.find(product => product.id === productId);

        if (!selectedProduct) {
          console.error(`Product with ID ${productId} not found.`);
          return;
        }

        setProductDetails(selectedProduct);
      } catch (error) {
        console.error("Error fetching product details:", error.message);
      }
    };

    fetchProductDetails();
  }, [productId]);

  return (
    <div>
      {productDetails ? (
        <div>
          <h2>Product Details for Product ID: {productId}</h2>
          <h3>{productDetails.name}</h3>
          <p>Current Price: ${productDetails.price}</p>
          {/* Display other product details */}
        </div>
      ) : (
        <p>Loading product details...</p>
      )}
    </div>
  );
};

export default UserProductDetails;
