import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { useSelector } from "react-redux";

const ProductDetail = () => {
  const user = useSelector((state) => state.user);
  const [userPrice, setUserPrice] = useState(product ? product.price : 0);
  const [userOffer, setUserOffer] = useState(null);

  const handleBargainSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(
        `/api/products/updateOffer/${product.name}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            userId: user.email,
            userOffer: userPrice,
          }),
        }
      );

      if (response.ok) {
        setUserOffer(userPrice);
      } else {
        console.error("Failed to update product with offer");
      }
    } catch (error) {
      console.error("Error making API call:", error);
    }
  };

  return (
    <div className="container mx-auto my-8 p-4 bg-white shadow-lg rounded-lg">
      <>
        <h2 className="text-2xl font-semibold mb-4">{product.name}</h2>
        <p className="text-gray-700 mb-4">{product.description}</p>
        <p className="text-gray-600 mb-2">Category: {product.category}</p>
        <p className="text-gray-600 mb-2">ID: {product.studentId}</p>
        <p className="text-gray-600 mb-2">Price: ${product.price}</p>
        <p className="text-gray-600 mb-2">User's Offer: ${userOffer}</p>
        <img className="mt-4" src={product.productPath} alt={product.name} />

        {/* Bargain form */}
        <form onSubmit={handleBargainSubmit}>
          <label htmlFor="userPrice" className="block text-gray-600 mt-4">
            Bargain Price: ${userPrice}
          </label>
          <input
            type="range"
            id="userPrice"
            value={userPrice}
            min={0}
            max={product.price * 2}
            onChange={(e) => setUserPrice(parseInt(e.target.value))}
            className="mt-2"
          />
          <br />
          <button type="submit" className="bg-blue-500 text-white p-2 mt-2">
            Make Offer
          </button>
        </form>
      </>
    </div>
  );
};

export default ProductDetail;
